import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import { ThemeProvider, createTheme, Container, Typography, Select, MenuItem, FormControl, InputLabel, Box, Tabs, Tab } from '@mui/material';
import axios from 'axios';
import QuestionBlock from './components/QuestionBlock';
import CourseLinks from './components/CourseLinks';
import PracticeSection from './components/PracticeSection';
import AIPracticeSection from './components/AIPracticeSection';
import PredictionSection from './components/PredictionSection';
import ExamStructureSection from './components/ExamStructureSection';
import Login from './pages/Login';
import Dashboard from './pages/Dashboard';
import PrivateRoute from './components/PrivateRoute';
import { questionData } from './data/questions';
import './styles/main.css';
import './App.css';

const theme = createTheme({
  palette: {
    primary: {
      main: '#1976d2',
    },
    secondary: {
      main: '#dc004e',
    },
  },
});

const API_URL = 'http://localhost:5000/api';

function App() {
  const [selectedSystem, setSelectedSystem] = useState(0); // 0: 會考系統, 1: 研發系統
  const [selectedCategory, setSelectedCategory] = useState('');
  const [categories, setCategories] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchCategories = async () => {
      try {
        const response = await axios.get(`${API_URL}/categories`);
        // 根據系統類型過濾分類
        const filteredCategories = response.data.filter(
          category => category.system === (selectedSystem === 0 ? 'exam' : 'rd')
        );
        setCategories(filteredCategories);
        setLoading(false);
      } catch (err) {
        console.error('Error fetching categories:', err);
        setError('獲取分類失敗：' + err.message);
        setLoading(false);
      }
    };

    fetchCategories();
  }, [selectedSystem]); // 當系統切換時重新獲取分類

  const handleSystemChange = (event, newValue) => {
    setSelectedSystem(newValue);
    setSelectedCategory(''); // 切換系統時重置分類選擇
  };

  const handleCategoryChange = (event) => {
    setSelectedCategory(event.target.value);
  };

  const renderContent = () => {
    switch (selectedCategory) {
      case 'course-links':
      case 'rd-course-links':
        return <PracticeSection />;
      case 'ai-math':
      case 'rd-ai-math':
        return <PracticeSection />;
      case 'exam-structure':
      case 'rd-exam-structure':
        return <ExamStructureSection />;
      case 'ai-practice':
      case 'rd-ai-practice':
        return <AIPracticeSection />;
      case 'prediction':
      case 'rd-prediction':
        return <PredictionSection />;
      default:
        return null;
    }
  };

  if (loading) {
    return <div className="loading">載入中...</div>;
  }

  if (error) {
    return <div className="error">{error}</div>;
  }

  return (
    <ThemeProvider theme={theme}>
      <Router>
        <Routes>
          <Route path="/" element={
            <Container maxWidth="lg" className="app-container">
              <Box sx={{ borderBottom: 1, borderColor: 'divider', mb: 3 }}>
                <Tabs 
                  value={selectedSystem} 
                  onChange={handleSystemChange}
                  centered
                  sx={{
                    '& .MuiTab-root': {
                      fontSize: '1.2rem',
                      fontWeight: 'bold',
                      minWidth: 200,
                    },
                  }}
                >
                  <Tab label="國中會考數學 AI 輔助學習系統" />
                  <Tab label="研發補助系統" />
                </Tabs>
              </Box>

              {selectedSystem === 0 ? (
                <>
                  <Typography variant="h4" component="h1" gutterBottom className="app-title">
                    會考AI助手
                  </Typography>

                  <FormControl fullWidth className="category-selector">
                    <InputLabel id="category-select-label">選擇功能</InputLabel>
                    <Select
                      labelId="category-select-label"
                      value={selectedCategory}
                      label="選擇功能"
                      onChange={handleCategoryChange}
                    >
                      <MenuItem value="">--請選擇--</MenuItem>
                      {categories.map((category) => (
                        <MenuItem key={category._id} value={category._id}>
                          {category.label}
                        </MenuItem>
                      ))}
                    </Select>
                  </FormControl>

                  {renderContent()}
                </>
              ) : (
                <>
                  <Typography variant="h4" component="h1" gutterBottom className="app-title">
                    研發補助系統
                  </Typography>

                  <FormControl fullWidth className="category-selector">
                    <InputLabel id="rd-category-select-label">選擇功能</InputLabel>
                    <Select
                      labelId="rd-category-select-label"
                      value={selectedCategory}
                      label="選擇功能"
                      onChange={handleCategoryChange}
                    >
                      <MenuItem value="">--請選擇--</MenuItem>
                      {categories.map((category) => (
                        <MenuItem key={category._id} value={category._id}>
                          {category.label}
                        </MenuItem>
                      ))}
                    </Select>
                  </FormControl>

                  {renderContent()}
                </>
              )}
            </Container>
          } />
          <Route path="/login" element={<Login />} />
          <Route
            path="/admin/*"
            element={
              <PrivateRoute>
                <Dashboard />
              </PrivateRoute>
            }
          />
        </Routes>
      </Router>
    </ThemeProvider>
  );
}

export default App;
