import React, { useState, useEffect } from 'react';
import {
  Box,
  Button,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  TextField,
  IconButton,
  Select,
  MenuItem,
  FormControl,
  InputLabel,
  CircularProgress,
  Alert,
  List,
  ListItem,
  ListItemText,
  ListItemSecondaryAction,
} from '@mui/material';
import { Edit as EditIcon, Add as AddIcon, Delete as DeleteIcon } from '@mui/icons-material';
import axios from 'axios';

const API_URL = 'http://localhost:5000/api';

function ContentManagement() {
  const [contents, setContents] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState('');
  const [open, setOpen] = useState(false);
  const [editingContent, setEditingContent] = useState(null);
  const [formData, setFormData] = useState({
    category: '',
    title: '',
    prompt: '',
    steps: [],
  });
  const [newStep, setNewStep] = useState('');
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  const categories = [
    { value: 'course-links', label: '課程相關連結' },
    { value: 'ai-math', label: 'AI怎麼應用在數學' },
    { value: 'exam-structure', label: '會考架構分析' },
    { value: 'ai-practice', label: 'AI實戰' },
    { value: 'prediction', label: '預測考題及個人分析報告' },
  ];

  // 獲取所有內容
  const fetchContents = async () => {
    try {
      setLoading(true);
      const response = await axios.get(`${API_URL}/contents`);
      setContents(response.data);
      setError(null);
    } catch (err) {
      setError('獲取內容失敗：' + err.message);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchContents();
  }, []);

  const handleCategoryChange = (e) => {
    setSelectedCategory(e.target.value);
  };

  const handleOpen = (content = null) => {
    if (content) {
      setEditingContent(content);
      setFormData({
        category: content.category,
        title: content.title,
        prompt: content.prompt || '',
        steps: content.steps || [],
      });
    } else {
      setEditingContent(null);
      setFormData({
        category: selectedCategory,
        title: '',
        prompt: '',
        steps: [],
      });
    }
    setOpen(true);
  };

  const handleClose = () => {
    setOpen(false);
    setEditingContent(null);
    setFormData({
      category: '',
      title: '',
      prompt: '',
      steps: [],
    });
    setNewStep('');
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({
      ...prev,
      [name]: value,
    }));
  };

  const handleAddStep = () => {
    if (newStep.trim()) {
      setFormData((prev) => ({
        ...prev,
        steps: [...prev.steps, newStep.trim()],
      }));
      setNewStep('');
    }
  };

  const handleRemoveStep = (index) => {
    setFormData((prev) => ({
      ...prev,
      steps: prev.steps.filter((_, i) => i !== index),
    }));
  };

  const handleSubmit = async () => {
    try {
      if (editingContent) {
        await axios.put(`${API_URL}/contents/${editingContent._id}`, formData);
      } else {
        await axios.post(`${API_URL}/contents`, formData);
      }
      handleClose();
      fetchContents();
    } catch (err) {
      setError('儲存失敗：' + err.message);
    }
  };

  if (loading) {
    return (
      <Box sx={{ display: 'flex', justifyContent: 'center', mt: 4 }}>
        <CircularProgress />
      </Box>
    );
  }

  return (
    <div className="app">
      <h1>內容管理系統</h1>
      
      {error && (
        <Alert severity="error" sx={{ mb: 2, mx: 2 }}>
          {error}
        </Alert>
      )}

      <div className="category-selector">
        <label htmlFor="category">選擇分類：</label>
        <select 
          id="category" 
          value={selectedCategory}
          onChange={handleCategoryChange}
        >
          <option value="">--請選擇--</option>
          {categories.map((category) => (
            <option key={category.value} value={category.value}>
              {category.label}
            </option>
          ))}
        </select>
        {selectedCategory && (
          <Button
            variant="contained"
            color="primary"
            startIcon={<AddIcon />}
            onClick={() => handleOpen()}
            sx={{ ml: 2 }}
          >
            新增內容
          </Button>
        )}
      </div>

      <div className="content-area">
        {contents
          .filter(content => content.category === selectedCategory)
          .map((content) => (
            <div key={content._id} className="welcome-box fade-in" style={{ position: 'relative' }}>
              <IconButton
                sx={{
                  position: 'absolute',
                  top: 8,
                  right: 8,
                  backgroundColor: 'rgba(255, 255, 255, 0.8)',
                  '&:hover': {
                    backgroundColor: 'rgba(255, 255, 255, 0.9)',
                  },
                }}
                onClick={() => handleOpen(content)}
              >
                <EditIcon />
              </IconButton>
              <div style={{display:'flex',flexDirection:'column',alignItems:'center',gap:'16px'}}>
                <h2 style={{
                  color:'var(--primary-color)',
                  fontWeight:'bold',
                  fontSize:'2rem',
                  marginBottom:'10px'
                }}>
                  {content.title}
                </h2>
                {content.prompt && (
                  <p style={{
                    fontSize:'1.2rem',
                    color:'#555',
                    maxWidth:'500px',
                    textAlign:'center'
                  }}>
                    {content.prompt}
                  </p>
                )}
                {content.steps && content.steps.length > 0 && (
                  <List>
                    {content.steps.map((step, index) => (
                      <ListItem key={index}>
                        <ListItemText primary={step} />
                      </ListItem>
                    ))}
                  </List>
                )}
              </div>
            </div>
          ))}
      </div>

      <Dialog open={open} onClose={handleClose} maxWidth="md" fullWidth>
        <DialogTitle>
          {editingContent ? '編輯內容' : '新增內容'}
        </DialogTitle>
        <DialogContent>
          <Box sx={{ mt: 2, display: 'flex', flexDirection: 'column', gap: 2 }}>
            <FormControl fullWidth>
              <InputLabel>類別</InputLabel>
              <Select
                name="category"
                value={formData.category}
                onChange={handleChange}
                label="類別"
              >
                {categories.map((category) => (
                  <MenuItem key={category.value} value={category.value}>
                    {category.label}
                  </MenuItem>
                ))}
              </Select>
            </FormControl>
            <TextField
              name="title"
              label="標題"
              value={formData.title}
              onChange={handleChange}
              fullWidth
            />
            <TextField
              name="prompt"
              label="提示詞"
              value={formData.prompt}
              onChange={handleChange}
              multiline
              rows={4}
              fullWidth
            />
            <Box sx={{ mt: 2 }}>
              <TextField
                label="新增步驟"
                value={newStep}
                onChange={(e) => setNewStep(e.target.value)}
                fullWidth
                sx={{ mb: 1 }}
              />
              <Button
                variant="outlined"
                onClick={handleAddStep}
                disabled={!newStep.trim()}
              >
                新增步驟
              </Button>
            </Box>
            <List>
              {formData.steps.map((step, index) => (
                <ListItem key={index}>
                  <ListItemText primary={step} />
                  <ListItemSecondaryAction>
                    <IconButton
                      edge="end"
                      aria-label="delete"
                      onClick={() => handleRemoveStep(index)}
                    >
                      <DeleteIcon />
                    </IconButton>
                  </ListItemSecondaryAction>
                </ListItem>
              ))}
            </List>
          </Box>
        </DialogContent>
        <DialogActions>
          <Button onClick={handleClose}>取消</Button>
          <Button onClick={handleSubmit} variant="contained" color="primary">
            儲存
          </Button>
        </DialogActions>
      </Dialog>
    </div>
  );
}

export default ContentManagement;
