import React, { useState, useEffect } from 'react';
import { Box, Typography, Card, CardContent, Button, CircularProgress, Alert } from '@mui/material';
import ContentCopyIcon from '@mui/icons-material/ContentCopy';
import axios from 'axios';
import '../styles/components/PracticeSection.css';

const API_URL = 'http://localhost:3001/api';

const PredictionSection = () => {
  const [predictions, setPredictions] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [category, setCategory] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      try {
        // 獲取分類信息
        const categoryResponse = await axios.get(`${API_URL}/categories`);
        const predictionCategory = categoryResponse.data.find(c => c.label === '預測考題及個人分析報告');
        setCategory(predictionCategory);

        // 獲取內容
        const response = await axios.get(`${API_URL}/contents?category=${predictionCategory.label}`);
        setPredictions(response.data);
        setError(null);
      } catch (err) {
        console.error('Error fetching data:', err);
        setError('獲取數據失敗');
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  const handleCopy = (text) => {
    navigator.clipboard.writeText(text);
  };

  if (loading) {
    return (
      <Box display="flex" justifyContent="center" alignItems="center" minHeight="200px">
        <CircularProgress />
      </Box>
    );
  }

  if (error) {
    return (
      <Box p={2}>
        <Alert 
          severity="error"
        >
          {error}
        </Alert>
      </Box>
    );
  }

  return (
    <div className="practice-section">
      <h2 className="section-title">{category?.label || '預測考題及個人分析報告'}</h2>
      <div className="practices-grid">
        {predictions.map((prediction, index) => (
          <div key={prediction._id || index} className="practice-card">
            <h3 className="practice-title">{prediction.title}</h3>
            <div className="prompt-box">
              <pre className="prompt-text">{prediction.prompt}</pre>
              <button 
                className="copy-button"
                onClick={() => handleCopy(prediction.prompt)}
              >
                複製提示詞
              </button>
              <div className="steps-box">
                <h4>操作步驟：</h4>
                <ol>
                  {prediction.steps.map((step, stepIndex) => (
                    <li key={stepIndex}>{step}</li>
                  ))}
                </ol>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default PredictionSection;
