import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import { ThemeProvider, createTheme, Container, Typography, Select, MenuItem, FormControl, InputLabel, Button, Box } from '@mui/material';
import axios from 'axios';
import QuestionBlock from './components/QuestionBlock';
import CourseLinks from './components/CourseLinks';
import PracticeSection from './components/PracticeSection';
import AIPracticeSection from './components/AIPracticeSection';
import PredictionSection from './components/PredictionSection';
import ExamStructureSection from './components/ExamStructureSection';
import Login from './pages/Login';
import Dashboard from './pages/Dashboard';
import PrivateRoute from './components/PrivateRoute';
import { questionData } from './data/questions';
import './styles/main.css';
import './App.css';

const theme = createTheme({
  palette: {
    primary: {
      main: '#1976d2',
    },
    secondary: {
      main: '#dc004e',
    },
  },
});

const API_URL = 'http://localhost:5000/api';

function App() {
  const [selectedCategory, setSelectedCategory] = useState('');
  const [categories, setCategories] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [currentSystem, setCurrentSystem] = useState('math'); // 'math' 或 'new'

  useEffect(() => {
    const fetchCategories = async () => {
      try {
        const response = await axios.get(`${API_URL}/categories`);
        setCategories(response.data);
        setLoading(false);
      } catch (err) {
        console.error('Error fetching categories:', err);
        setError('獲取分類失敗：' + err.message);
        setLoading(false);
      }
    };

    fetchCategories();
  }, []);

  const handleCategoryChange = (event) => {
    setSelectedCategory(event.target.value);
  };

  const handleSystemChange = () => {
    setCurrentSystem(currentSystem === 'math' ? 'new' : 'math');
    setSelectedCategory(''); // 重置選擇的分類
  };

  const renderContent = () => {
    if (currentSystem === 'new') {
      // 新系統的內容
      return (
        <div className="practice-section">
          <h2 className="section-title">新系統</h2>
          <div className="practices-grid">
            {/* 這裡可以放置新系統的內容 */}
            <div className="practice-card">
              <h3>新系統功能開發中</h3>
              <div className="practice-content">
                <p>敬請期待...</p>
              </div>
            </div>
          </div>
        </div>
      );
    }

    // 原有系統的內容
    switch (selectedCategory) {
      case 'course-links':
        return <CourseLinks />;
      case 'ai-math':
        return <PracticeSection />;
      case 'exam-structure':
        return <ExamStructureSection />;
      case 'ai-practice':
        return <AIPracticeSection />;
      case 'prediction':
        return <PredictionSection />;
      default:
        return (
          <div className="welcome-box fade-in">
            <div style={{display:'flex',flexDirection:'column',alignItems:'center',gap:'16px'}}>
              <img src="https://cdn-icons-png.flaticon.com/512/3135/3135768.png" alt="AI Icon" style={{width:'80px',height:'80px',marginBottom:'10px',filter:'drop-shadow(0 2px 8px #2196F355)'}}/>
              <h2 style={{color:'var(--primary-color)',fontWeight:'bold',fontSize:'2rem',marginBottom:'10px'}}>歡迎使用「國中會考數學 AI 輔助學習系統」</h2>
              <p style={{fontSize:'1.2rem',color:'#555',maxWidth:'500px',textAlign:'center'}}>本系統結合 <span style={{color:'var(--primary-color)',fontWeight:'bold'}}>AI 技術</span>，協助你針對會考數學進行練習、分析與規劃。<br/>請從上方選單選擇你想使用的功能開始探索！</p>
            </div>
          </div>
        );
    }
  };

  if (loading) {
    return <div className="loading">載入中...</div>;
  }

  if (error) {
    return <div className="error">{error}</div>;
  }

  return (
    <ThemeProvider theme={theme}>
      <Router>
        <Routes>
          <Route path="/" element={
            <Container maxWidth="lg" className="app-container">
              <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 3 }}>
                <Typography variant="h4" component="h1" className="app-title">
                  {currentSystem === 'math' ? '國中會考數學 AI 輔助學習系統' : '新系統'}
                </Typography>
                <Button 
                  variant="contained" 
                  color="primary" 
                  onClick={handleSystemChange}
                  sx={{ minWidth: '120px' }}
                >
                  切換到{currentSystem === 'math' ? '新系統' : '數學系統'}
                </Button>
              </Box>

              {currentSystem === 'math' && (
                <FormControl fullWidth className="category-selector">
                  <InputLabel id="category-select-label">選擇功能</InputLabel>
                  <Select
                    labelId="category-select-label"
                    value={selectedCategory}
                    label="選擇功能"
                    onChange={handleCategoryChange}
                  >
                    <MenuItem value="">--請選擇--</MenuItem>
                    {categories.map((category) => (
                      <MenuItem key={category._id} value={category._id}>
                        {category.label}
                      </MenuItem>
                    ))}
                  </Select>
                </FormControl>
              )}

              {renderContent()}
            </Container>
          } />
          <Route path="/login" element={<Login />} />
          <Route
            path="/admin/*"
            element={
              <PrivateRoute>
                <Dashboard />
              </PrivateRoute>
            }
          />
        </Routes>
      </Router>
    </ThemeProvider>
  );
}

export default App;
