import React, { useState, useEffect } from 'react';
import axios from 'axios';
import '../styles/components/PracticeSection.css';

const API_URL = 'http://localhost:5000/api';

function PredictionSection() {
  const [predictions, setPredictions] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchPredictions = async () => {
      try {
        console.log('Fetching predictions...');
        const response = await axios.get(`${API_URL}/contents?category=預測考題及個人分析報告`);
        console.log('Received predictions:', response.data);
        setPredictions(response.data);
        setError(null);
      } catch (err) {
        console.error('Error fetching predictions:', err);
        setError(err.response?.data?.error || '獲取數據失敗');
      } finally {
        setLoading(false);
      }
    };

    fetchPredictions();
  }, []);

  const handleCopy = (text) => {
    navigator.clipboard.writeText(text);
  };

  if (loading) return <div className="loading">載入中...</div>;
  if (error) return (
    <div className="error">
      <p>{error}</p>
      <button onClick={() => window.location.reload()}>重試</button>
    </div>
  );

  return (
    <section className="practice-section">
      <h2 className="section-title">預測考題及個人分析報告</h2>
      <div className="practice-grid">
        {predictions.map((prediction) => (
          <div key={prediction._id} className="practice-card">
            <h3>{prediction.title}</h3>
            <p>{prediction.prompt}</p>
            {prediction.steps && prediction.steps.length > 0 && (
              <div className="steps">
                <h4>操作步驟：</h4>
                <ol>
                  {prediction.steps.map((step, index) => (
                    <li key={index}>{step}</li>
                  ))}
                </ol>
              </div>
            )}
            <button 
              className="copy-button"
              onClick={() => handleCopy(prediction.prompt)}
            >
              複製提示詞
            </button>
          </div>
        ))}
      </div>
    </section>
  );
}

export default PredictionSection;
