import React, { useState, useEffect } from 'react';
import {
  Box,
  Container,
  Typography,
  Button,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  TextField,
  IconButton,
  Select,
  MenuItem,
  FormControl,
  InputLabel,
  CircularProgress,
  Alert,
  Card,
  CardContent,
  Grid,
  Paper,
  Divider,
} from '@mui/material';
import { Edit as EditIcon, Delete as DeleteIcon, Add as AddIcon } from '@mui/icons-material';
import axios from 'axios';

const API_URL = 'http://localhost:5000/api';

function ContentManagement() {
  const [contents, setContents] = useState([]);
  const [filteredContents, setFilteredContents] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState('');
  const [open, setOpen] = useState(false);
  const [editingContent, setEditingContent] = useState(null);
  const [formData, setFormData] = useState({
    category: '',
    title: '',
    prompt: '',
    steps: [],
  });
  const [newStep, setNewStep] = useState('');
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  const categories = [
    { value: 'course-links', label: '課程相關連結' },
    { value: 'ai-math', label: 'AI怎麼應用在數學' },
    { value: 'exam-structure', label: '會考架構分析' },
    { value: 'ai-practice', label: 'AI實戰' },
    { value: 'prediction', label: '預測考題及個人分析報告' },
  ];

  // 獲取所有內容
  const fetchContents = async () => {
    try {
      setLoading(true);
      const response = await axios.get(`${API_URL}/contents`);
      setContents(response.data);
      setFilteredContents(response.data);
      setError(null);
    } catch (err) {
      setError('獲取內容失敗：' + err.message);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchContents();
  }, []);

  // 處理分類過濾
  useEffect(() => {
    if (selectedCategory) {
      setFilteredContents(contents.filter(content => content.category === selectedCategory));
    } else {
      setFilteredContents(contents);
    }
  }, [selectedCategory, contents]);

  const handleCategoryChange = (event) => {
    setSelectedCategory(event.target.value);
  };

  const handleOpen = () => {
    setOpen(true);
  };

  const handleClose = () => {
    setOpen(false);
    setEditingContent(null);
    setFormData({
      category: '',
      title: '',
      prompt: '',
      steps: [],
    });
    setNewStep('');
  };

  const handleEdit = (content) => {
    setEditingContent(content);
    setFormData({
      category: content.category,
      title: content.title,
      prompt: content.prompt,
      steps: content.steps,
    });
    setOpen(true);
  };

  const handleDelete = async (id) => {
    if (window.confirm('確定要刪除這個內容嗎？')) {
      try {
        await axios.delete(`${API_URL}/contents/${id}`);
        fetchContents();
      } catch (err) {
        setError('刪除內容失敗：' + err.message);
      }
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      if (editingContent) {
        await axios.put(`${API_URL}/contents/${editingContent._id}`, formData);
      } else {
        await axios.post(`${API_URL}/contents`, formData);
      }
      handleClose();
      fetchContents();
    } catch (err) {
      setError('保存內容失敗：' + err.message);
    }
  };

  const handleAddStep = () => {
    if (newStep.trim()) {
      setFormData(prev => ({
        ...prev,
        steps: [...prev.steps, newStep.trim()]
      }));
      setNewStep('');
    }
  };

  const handleRemoveStep = (index) => {
    setFormData(prev => ({
      ...prev,
      steps: prev.steps.filter((_, i) => i !== index)
    }));
  };

  return (
    <Container maxWidth="lg" sx={{ mt: 4, mb: 4 }}>
      <Typography variant="h4" component="h1" gutterBottom sx={{ 
        fontWeight: 'bold',
        color: 'primary.main',
        textAlign: 'center',
        mb: 4
      }}>
        內容管理系統
      </Typography>

      <Paper 
        elevation={3}
        sx={{ 
          p: 3, 
          mb: 4,
          borderRadius: 2,
          backgroundColor: 'background.paper'
        }}
      >
        <Grid container spacing={3} alignItems="center">
          <Grid item xs={12} md={6}>
            <FormControl fullWidth>
              <InputLabel>選擇分類</InputLabel>
              <Select
                value={selectedCategory}
                onChange={handleCategoryChange}
                label="選擇分類"
                sx={{ 
                  height: 56,
                  minWidth: 200,
                  '& .MuiSelect-select': {
                    fontSize: '1.1rem'
                  }
                }}
              >
                <MenuItem value="">全部內容</MenuItem>
                {categories.map((category) => (
                  <MenuItem key={category.value} value={category.value}>
                    {category.label}
                  </MenuItem>
                ))}
              </Select>
            </FormControl>
          </Grid>
          <Grid item xs={12} md={6} sx={{ textAlign: { xs: 'center', md: 'right' } }}>
            <Button
              variant="contained"
              startIcon={<AddIcon />}
              onClick={handleOpen}
              size="large"
              sx={{ 
                px: 4,
                py: 1.5,
                borderRadius: 2,
                textTransform: 'none',
                fontSize: '1.1rem'
              }}
            >
              新增內容
            </Button>
          </Grid>
        </Grid>
      </Paper>

      {error && (
        <Alert 
          severity="error" 
          sx={{ 
            mb: 3,
            borderRadius: 2,
            '& .MuiAlert-message': {
              fontSize: '1rem'
            }
          }}
        >
          {error}
        </Alert>
      )}

      {loading ? (
        <Box display="flex" justifyContent="center" p={4}>
          <CircularProgress size={40} />
        </Box>
      ) : (
        <Grid container spacing={3}>
          {filteredContents.map((content) => (
            <Grid item xs={12} key={content._id}>
              <Card 
                elevation={2}
                sx={{ 
                  borderRadius: 2,
                  transition: 'transform 0.2s, box-shadow 0.2s',
                  '&:hover': {
                    transform: 'translateY(-4px)',
                    boxShadow: 4
                  },
                  height: '100%',
                  display: 'flex',
                  flexDirection: 'column'
                }}
              >
                <CardContent sx={{ p: 3, flex: 1, display: 'flex', flexDirection: 'column' }}>
                  <Box display="flex" justifyContent="space-between" alignItems="flex-start" mb={2}>
                    <Box flex={1}>
                      <Typography 
                        variant="h5" 
                        component="div" 
                        sx={{ 
                          fontWeight: 'bold',
                          mb: 1,
                          color: 'primary.main'
                        }}
                      >
                        {content.title}
                      </Typography>
                      <Typography 
                        color="textSecondary" 
                        sx={{ 
                          fontSize: '1rem',
                          mb: 2
                        }}
                      >
                        分類：{categories.find(c => c.value === content.category)?.label || content.category}
                      </Typography>
                    </Box>
                    <Box sx={{ display: 'flex', gap: 2 }}>
                      <IconButton 
                        onClick={() => handleEdit(content)}
                        sx={{ 
                          color: 'primary.main',
                          '&:hover': { backgroundColor: 'primary.light' },
                          width: 40,
                          height: 40
                        }}
                      >
                        <EditIcon />
                      </IconButton>
                      <IconButton 
                        onClick={() => handleDelete(content._id)}
                        sx={{ 
                          color: 'error.main',
                          '&:hover': { backgroundColor: 'error.light' },
                          width: 40,
                          height: 40
                        }}
                      >
                        <DeleteIcon />
                      </IconButton>
                    </Box>
                  </Box>

                  <Divider sx={{ my: 2 }} />

                  <Typography 
                    variant="body1" 
                    component="pre" 
                    sx={{ 
                      whiteSpace: 'pre-wrap',
                      fontFamily: 'inherit',
                      fontSize: '1.1rem',
                      lineHeight: 1.6,
                      backgroundColor: 'grey.50',
                      p: 2,
                      borderRadius: 1,
                      mb: 2,
                      flex: 1,
                      minHeight: 100
                    }}
                  >
                    {content.prompt}
                  </Typography>

                  {content.steps && content.steps.length > 0 && (
                    <Box mt={3}>
                      <Typography 
                        variant="h6" 
                        sx={{ 
                          fontWeight: 'bold',
                          mb: 2,
                          color: 'primary.main'
                        }}
                      >
                        操作步驟：
                      </Typography>
                      <Box 
                        component="ol" 
                        sx={{ 
                          pl: 3,
                          '& li': {
                            mb: 2,
                            fontSize: '1.1rem',
                            lineHeight: 1.6
                          }
                        }}
                      >
                        {content.steps.map((step, index) => (
                          <li key={index}>
                            <Typography variant="body1">{step}</Typography>
                          </li>
                        ))}
                      </Box>
                    </Box>
                  )}
                </CardContent>
              </Card>
            </Grid>
          ))}
        </Grid>
      )}

      <Dialog 
        open={open} 
        onClose={handleClose} 
        maxWidth="md" 
        fullWidth
        PaperProps={{
          sx: {
            borderRadius: 2,
            p: 1,
            minHeight: 600
          }
        }}
      >
        <DialogTitle sx={{ 
          fontSize: '1.5rem',
          fontWeight: 'bold',
          color: 'primary.main',
          pb: 2
        }}>
          {editingContent ? '編輯內容' : '新增內容'}
        </DialogTitle>
        <DialogContent>
          <Box 
            component="form" 
            onSubmit={handleSubmit} 
            sx={{ 
              mt: 2,
              '& .MuiFormControl-root': {
                mb: 3
              }
            }}
          >
            <FormControl fullWidth>
              <InputLabel>分類</InputLabel>
              <Select
                value={formData.category}
                onChange={(e) => setFormData({ ...formData, category: e.target.value })}
                label="分類"
                required
                sx={{ 
                  height: 56,
                  '& .MuiSelect-select': {
                    fontSize: '1.1rem'
                  }
                }}
              >
                {categories.map((category) => (
                  <MenuItem key={category.value} value={category.value}>
                    {category.label}
                  </MenuItem>
                ))}
              </Select>
            </FormControl>

            <TextField
              fullWidth
              label="標題"
              value={formData.title}
              onChange={(e) => setFormData({ ...formData, title: e.target.value })}
              required
              sx={{ mb: 3 }}
            />

            <TextField
              fullWidth
              label="提示詞"
              value={formData.prompt}
              onChange={(e) => setFormData({ ...formData, prompt: e.target.value })}
              multiline
              rows={4}
              required
              sx={{ mb: 3 }}
            />

            <Box sx={{ mb: 3 }}>
              <Typography 
                variant="h6" 
                sx={{ 
                  fontWeight: 'bold',
                  mb: 2,
                  color: 'primary.main'
                }}
              >
                操作步驟
              </Typography>
              {formData.steps.map((step, index) => (
                <Box 
                  key={index} 
                  display="flex" 
                  alignItems="center" 
                  mb={2}
                  sx={{
                    '& .MuiTextField-root': {
                      mr: 1
                    }
                  }}
                >
                  <Typography 
                    sx={{ 
                      mr: 2,
                      fontWeight: 'bold',
                      color: 'primary.main',
                      minWidth: '2rem'
                    }}
                  >
                    {index + 1}.
                  </Typography>
                  <TextField
                    fullWidth
                    value={step}
                    onChange={(e) => {
                      const newSteps = [...formData.steps];
                      newSteps[index] = e.target.value;
                      setFormData({ ...formData, steps: newSteps });
                    }}
                    multiline
                    rows={2}
                  />
                  <IconButton 
                    onClick={() => handleRemoveStep(index)}
                    sx={{ 
                      color: 'error.main',
                      '&:hover': { backgroundColor: 'error.light' },
                      ml: 1
                    }}
                  >
                    <DeleteIcon />
                  </IconButton>
                </Box>
              ))}
              <Box 
                display="flex" 
                mt={2}
                sx={{
                  '& .MuiTextField-root': {
                    mr: 1
                  }
                }}
              >
                <TextField
                  fullWidth
                  label="新增步驟"
                  value={newStep}
                  onChange={(e) => setNewStep(e.target.value)}
                  multiline
                  rows={2}
                />
                <Button 
                  onClick={handleAddStep} 
                  variant="outlined"
                  sx={{ 
                    px: 3,
                    textTransform: 'none',
                    fontSize: '1rem',
                    ml: 1
                  }}
                >
                  添加
                </Button>
              </Box>
            </Box>
          </Box>
        </DialogContent>
        <DialogActions sx={{ p: 3 }}>
          <Button 
            onClick={handleClose}
            sx={{ 
              mr: 1,
              px: 3,
              textTransform: 'none',
              fontSize: '1rem'
            }}
          >
            取消
          </Button>
          <Button 
            onClick={handleSubmit} 
            variant="contained"
            sx={{ 
              px: 3,
              textTransform: 'none',
              fontSize: '1rem'
            }}
          >
            保存
          </Button>
        </DialogActions>
      </Dialog>
    </Container>
  );
}

export default ContentManagement;
