import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import { ThemeProvider, createTheme, Container, Typography, Box, Button, Paper } from '@mui/material';
import QuestionBlock from './components/QuestionBlock';
import CourseLinks from './components/CourseLinks';
import PracticeSection from './components/PracticeSection';
import AIPracticeSection from './components/AIPracticeSection';
import PredictionSection from './components/PredictionSection';
import ExamStructureSection from './components/ExamStructureSection';
import Login from './pages/Login';
import Dashboard from './pages/Dashboard';
import PrivateRoute from './components/PrivateRoute';
import { questionData } from './data/questions';
import './styles/main.css';
import './App.css';

const theme = createTheme({
  palette: {
    primary: {
      main: '#1976d2',
    },
    secondary: {
      main: '#dc004e',
    },
  },
});

function App() {
  const [selectedSystem, setSelectedSystem] = useState('');
  const [selectedCategory, setSelectedCategory] = useState('');

  const handleSystemSelect = (system) => {
    setSelectedSystem(system);
    setSelectedCategory('');
  };

  const handleCategoryChange = (event) => {
    setSelectedCategory(event.target.value);
  };

  const renderContent = () => {
    if (!selectedSystem) {
      return (
        <Box className="system-selection">
          <Paper elevation={3} className="system-card" onClick={() => handleSystemSelect('exam')}>
            <Typography variant="h5" component="h2" gutterBottom>
              國中會考數學 AI 輔助學習系統
            </Typography>
            <Typography variant="body1" color="textSecondary">
              提供會考數學相關的AI輔助學習功能，包括練習、分析和預測。
            </Typography>
          </Paper>

          <Paper elevation={3} className="system-card" onClick={() => handleSystemSelect('research')}>
            <Typography variant="h5" component="h2" gutterBottom>
              系統研發補助
            </Typography>
            <Typography variant="body1" color="textSecondary">
              提供系統研發相關的輔助工具和資源。
            </Typography>
          </Paper>
        </Box>
      );
    }

    if (selectedSystem === 'exam') {
      return (
        <div className="exam-system">
          <div className="category-selector">
            <select 
              value={selectedCategory}
              onChange={handleCategoryChange}
            >
              <option value="">--請選擇功能--</option>
              <option value="course-links">課程相關連結</option>
              <option value="ai-math">AI怎麼應用在數學</option>
              <option value="exam-structure">會考架構分析</option>
              <option value="ai-practice">AI實戰</option>
              <option value="prediction">預測考題及個人分析報告</option>
            </select>
          </div>

          <div className="content-area">
            {selectedCategory === '' ? (
              <div className="welcome-box fade-in">
                <div style={{display:'flex',flexDirection:'column',alignItems:'center',gap:'16px'}}>
                  <img src="https://cdn-icons-png.flaticon.com/512/3135/3135768.png" alt="AI Icon" style={{width:'80px',height:'80px',marginBottom:'10px',filter:'drop-shadow(0 2px 8px #2196F355)'}}/>
                  <h2 style={{color:'var(--primary-color)',fontWeight:'bold',fontSize:'2rem',marginBottom:'10px'}}>歡迎使用「國中會考數學 AI 輔助學習系統」</h2>
                  <p style={{fontSize:'1.2rem',color:'#555',maxWidth:'500px',textAlign:'center'}}>本系統結合 <span style={{color:'var(--primary-color)',fontWeight:'bold'}}>AI 技術</span>，協助你針對會考數學進行練習、分析與規劃。<br/>請從上方選單選擇你想使用的功能開始探索！</p>
                </div>
              </div>
            ) : selectedCategory === 'course-links' ? (
              <PracticeSection />
            ) : selectedCategory === 'ai-math' ? (
              <PracticeSection />
            ) : selectedCategory === 'exam-structure' ? (
              <ExamStructureSection />
            ) : selectedCategory === 'ai-practice' ? (
              <AIPracticeSection />
            ) : selectedCategory === 'prediction' ? (
              <PredictionSection />
            ) : null}
          </div>
        </div>
      );
    }

    if (selectedSystem === 'research') {
      return (
        <div className="research-system">
          <div className="category-selector">
            <select 
              value={selectedCategory}
              onChange={handleCategoryChange}
            >
              <option value="">--請選擇功能--</option>
              <option value="research-tools">研發工具</option>
              <option value="resources">資源庫</option>
              <option value="documentation">文檔中心</option>
            </select>
          </div>

          <div className="content-area">
            {selectedCategory === '' ? (
              <div className="welcome-box fade-in">
                <div style={{display:'flex',flexDirection:'column',alignItems:'center',gap:'16px'}}>
                  <img src="https://cdn-icons-png.flaticon.com/512/3135/3135768.png" alt="Research Icon" style={{width:'80px',height:'80px',marginBottom:'10px',filter:'drop-shadow(0 2px 8px #2196F355)'}}/>
                  <h2 style={{color:'var(--primary-color)',fontWeight:'bold',fontSize:'2rem',marginBottom:'10px'}}>歡迎使用「系統研發補助」</h2>
                  <p style={{fontSize:'1.2rem',color:'#555',maxWidth:'500px',textAlign:'center'}}>本系統提供研發相關的工具和資源，協助你更有效率地進行系統開發。<br/>請從上方選單選擇你想使用的功能開始探索！</p>
                </div>
              </div>
            ) : (
              <div className="research-content">
                {/* 這裡可以添加研發系統的具體內容組件 */}
                <Typography variant="h4">研發系統功能開發中...</Typography>
              </div>
            )}
          </div>
        </div>
      );
    }
  };

  return (
    <ThemeProvider theme={theme}>
      <Router>
        <Routes>
          <Route path="/" element={<App />} />
          <Route path="/login" element={<Login />} />
          <Route
            path="/admin/*"
            element={
              <PrivateRoute>
                <Dashboard />
              </PrivateRoute>
            }
          />
        </Routes>
      </Router>
    </ThemeProvider>
  );
}

export default App;
