import React, { useState, useEffect } from 'react';
import { Box, Typography, Grid, Card, CardContent, Button, CircularProgress, Alert } from '@mui/material';
import axios from 'axios';
import '../styles/PracticeSection.css';

const API_URL = 'http://localhost:3001/api';

function PredictionSection() {
  const [predictions, setPredictions] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [retryCount, setRetryCount] = useState(0);

  const fetchPredictions = async () => {
    try {
      console.log('Fetching predictions...');
      const response = await axios.get(`${API_URL}/contents?category=預測考題及個人分析報告`);
      console.log('Predictions fetched:', response.data);
      setPredictions(response.data);
      setError(null);
    } catch (err) {
      console.error('Error fetching predictions:', err);
      setError('獲取預測內容失敗');
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchPredictions();
  }, [retryCount]);

  const handleRetry = () => {
    setRetryCount(prev => prev + 1);
  };

  const handleCopyPrompt = (prompt) => {
    navigator.clipboard.writeText(prompt);
  };

  if (loading) {
    return (
      <Box display="flex" justifyContent="center" p={4}>
        <CircularProgress />
      </Box>
    );
  }

  if (error) {
    return (
      <Alert 
        severity="error" 
        sx={{ 
          mb: 3,
          borderRadius: 2,
          '& .MuiAlert-message': {
            fontSize: '1rem'
          }
        }}
        action={
          <Button color="inherit" size="small" onClick={handleRetry}>
            重試
          </Button>
        }
      >
        {error}
      </Alert>
    );
  }

  return (
    <div className="practice-section">
      <h2 className="section-title">預測考題及個人分析報告</h2>
      <div className="practice-grid">
        {predictions.map((prediction, index) => (
          <div key={prediction._id} className="practice-card">
            <div className="practice-content">
              <h3 className="practice-title">{prediction.title}</h3>
              <p className="practice-prompt">{prediction.prompt}</p>
              {prediction.steps && prediction.steps.length > 0 && (
                <div className="practice-steps">
                  <h4>操作步驟：</h4>
                  <ol>
                    {prediction.steps.map((step, stepIndex) => (
                      <li key={stepIndex}>{step}</li>
                    ))}
                  </ol>
                </div>
              )}
              <button 
                className="copy-button"
                onClick={() => handleCopyPrompt(prediction.prompt)}
              >
                複製提示詞
              </button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default PredictionSection;
