import React, { useState, useEffect } from 'react';
import { Box, Typography, Card, CardContent, Button, CircularProgress, Alert } from '@mui/material';
import ContentCopyIcon from '@mui/icons-material/ContentCopy';
import axios from 'axios';

const API_URL = 'http://localhost:5000/api';

const PredictionSection = () => {
  const [predictions, setPredictions] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchPredictions = async () => {
      try {
        const response = await axios.get(`${API_URL}/contents?category=prediction`);
        setPredictions(response.data);
        setError(null);
      } catch (err) {
        setError('獲取預測內容失敗：' + err.message);
      } finally {
        setLoading(false);
      }
    };

    fetchPredictions();
  }, []);

  const handleCopy = (text) => {
    navigator.clipboard.writeText(text);
  };

  if (loading) {
    return (
      <Box display="flex" justifyContent="center" alignItems="center" minHeight="200px">
        <CircularProgress />
      </Box>
    );
  }

  if (error) {
    return (
      <Box p={2}>
        <Alert severity="error">{error}</Alert>
      </Box>
    );
  }

  return (
    <Box>
      {predictions.map((prediction, index) => (
        <Card key={prediction._id || index} sx={{ mb: 2 }}>
          <CardContent>
            <Typography variant="h6" gutterBottom>
              {prediction.title}
            </Typography>
            <Typography variant="body1" paragraph>
              {prediction.prompt}
            </Typography>
            <Box component="ol" sx={{ pl: 2 }}>
              {prediction.steps.map((step, stepIndex) => (
                <Box component="li" key={stepIndex} sx={{ mb: 1 }}>
                  <Typography variant="body2">{step}</Typography>
                </Box>
              ))}
            </Box>
            <Button
              variant="outlined"
              startIcon={<ContentCopyIcon />}
              onClick={() => handleCopy(prediction.prompt)}
              size="small"
            >
              複製提示詞
            </Button>
          </CardContent>
        </Card>
      ))}
    </Box>
  );
};

export default PredictionSection;
