import React, { useState, useEffect } from 'react';
import axios from 'axios';
import '../styles/PracticeSection.css';

const API_URL = 'http://localhost:3001/api';

function ExamStructureSection() {
  const [structures, setStructures] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [category, setCategory] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      try {
        console.log('Fetching category data...');
        const categoryResponse = await axios.get(`${API_URL}/categories`);
        console.log('Category response:', categoryResponse.data);

        const structureCategory = categoryResponse.data.find(c => c.label === '會考架構分析');
        console.log('Found structure category:', structureCategory);

        if (!structureCategory) {
          throw new Error('找不到會考架構分析分類');
        }

        setCategory(structureCategory);

        console.log('Fetching contents for category:', structureCategory.label);
        const response = await axios.get(`${API_URL}/contents?category=${structureCategory.label}`);
        console.log('Contents response:', response.data);

        setStructures(response.data);
        setError(null);
      } catch (err) {
        console.error('Error in ExamStructureSection:', err);
        setError(err.response?.data?.error || err.message || '獲取數據失敗');
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  const handleCopy = (text) => {
    navigator.clipboard.writeText(text);
  };

  if (loading) return <div className="loading">載入中...</div>;
  if (error) return (
    <div className="error">
      <p>{error}</p>
      <button onClick={() => window.location.reload()}>重試</button>
    </div>
  );

  return (
    <section className="practice-section">
      <h2 className="section-title">{category?.label || '會考架構分析'}</h2>
      <div className="practice-grid">
        {structures.map((structure) => (
          <div key={structure._id} className="practice-card">
            <h3>{structure.title}</h3>
            <p>{structure.prompt}</p>
            {structure.steps && structure.steps.length > 0 && (
              <div className="steps">
                <h4>操作步驟：</h4>
                <ol>
                  {structure.steps.map((step, index) => (
                    <li key={index}>{step}</li>
                  ))}
                </ol>
              </div>
            )}
            <button 
              className="copy-button"
              onClick={() => handleCopy(structure.prompt)}
            >
              複製提示詞
            </button>
          </div>
        ))}
      </div>
    </section>
  );
}

export default ExamStructureSection;
