import React, { useState } from 'react';
import {
  Box,
  Container,
  Typography,
  Paper,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Button,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  TextField,
  IconButton,
  Select,
  MenuItem,
  FormControl,
  InputLabel,
} from '@mui/material';
import { Edit as EditIcon, Delete as DeleteIcon } from '@mui/icons-material';

// 模擬數據
const initialContent = [
  {
    id: 1,
    category: 'course-links',
    title: '課程相關連結',
    content: '課程內容...',
  },
  {
    id: 2,
    category: 'ai-math',
    title: 'AI怎麼應用在數學',
    content: 'AI應用內容...',
  },
  // 可以添加更多初始數據
];

function ContentManagement() {
  const [contents, setContents] = useState(initialContent);
  const [open, setOpen] = useState(false);
  const [editingContent, setEditingContent] = useState(null);
  const [formData, setFormData] = useState({
    category: '',
    title: '',
    content: '',
  });

  const categories = [
    { value: 'course-links', label: '課程相關連結' },
    { value: 'ai-math', label: 'AI怎麼應用在數學' },
    { value: 'exam-structure', label: '會考架構分析' },
    { value: 'ai-practice', label: 'AI實戰' },
    { value: 'prediction', label: '預測考題及個人分析報告' },
  ];

  const handleOpen = (content = null) => {
    if (content) {
      setEditingContent(content);
      setFormData({
        category: content.category,
        title: content.title,
        content: content.content,
      });
    } else {
      setEditingContent(null);
      setFormData({
        category: '',
        title: '',
        content: '',
      });
    }
    setOpen(true);
  };

  const handleClose = () => {
    setOpen(false);
    setEditingContent(null);
    setFormData({
      category: '',
      title: '',
      content: '',
    });
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({
      ...prev,
      [name]: value,
    }));
  };

  const handleSubmit = () => {
    if (editingContent) {
      // 更新現有內容
      setContents((prev) =>
        prev.map((item) =>
          item.id === editingContent.id
            ? { ...item, ...formData }
            : item
        )
      );
    } else {
      // 添加新內容
      setContents((prev) => [
        ...prev,
        {
          id: Date.now(),
          ...formData,
        },
      ]);
    }
    handleClose();
  };

  const handleDelete = (id) => {
    if (window.confirm('確定要刪除這筆內容嗎？')) {
      setContents((prev) => prev.filter((item) => item.id !== id));
    }
  };

  return (
    <Container maxWidth="lg" sx={{ mt: 4, mb: 4 }}>
      <Box sx={{ display: 'flex', justifyContent: 'space-between', mb: 3 }}>
        <Typography variant="h4" component="h1">
          內容管理
        </Typography>
        <Button
          variant="contained"
          color="primary"
          onClick={() => handleOpen()}
        >
          新增內容
        </Button>
      </Box>

      <TableContainer component={Paper}>
        <Table>
          <TableHead>
            <TableRow>
              <TableCell>類別</TableCell>
              <TableCell>標題</TableCell>
              <TableCell>內容</TableCell>
              <TableCell align="right">操作</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {contents.map((content) => (
              <TableRow key={content.id}>
                <TableCell>
                  {categories.find((cat) => cat.value === content.category)?.label}
                </TableCell>
                <TableCell>{content.title}</TableCell>
                <TableCell>
                  {content.content.length > 50
                    ? `${content.content.substring(0, 50)}...`
                    : content.content}
                </TableCell>
                <TableCell align="right">
                  <IconButton
                    color="primary"
                    onClick={() => handleOpen(content)}
                  >
                    <EditIcon />
                  </IconButton>
                  <IconButton
                    color="error"
                    onClick={() => handleDelete(content.id)}
                  >
                    <DeleteIcon />
                  </IconButton>
                </TableCell>
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </TableContainer>

      <Dialog open={open} onClose={handleClose} maxWidth="md" fullWidth>
        <DialogTitle>
          {editingContent ? '編輯內容' : '新增內容'}
        </DialogTitle>
        <DialogContent>
          <Box sx={{ mt: 2, display: 'flex', flexDirection: 'column', gap: 2 }}>
            <FormControl fullWidth>
              <InputLabel>類別</InputLabel>
              <Select
                name="category"
                value={formData.category}
                onChange={handleChange}
                label="類別"
              >
                {categories.map((category) => (
                  <MenuItem key={category.value} value={category.value}>
                    {category.label}
                  </MenuItem>
                ))}
              </Select>
            </FormControl>
            <TextField
              name="title"
              label="標題"
              value={formData.title}
              onChange={handleChange}
              fullWidth
            />
            <TextField
              name="content"
              label="內容"
              value={formData.content}
              onChange={handleChange}
              multiline
              rows={4}
              fullWidth
            />
          </Box>
        </DialogContent>
        <DialogActions>
          <Button onClick={handleClose}>取消</Button>
          <Button onClick={handleSubmit} variant="contained" color="primary">
            儲存
          </Button>
        </DialogActions>
      </Dialog>
    </Container>
  );
}

export default ContentManagement;
