import React from 'react';
import { Link } from 'react-router-dom';
import SectionHeader from '../components/SectionHeader';
import '../styles/pages/Home.css';

const Home = () => {
  return (
    <div className="home-page">
      <SectionHeader title="統測練習系統" />
      
      <div className="intro-section">
        <h3>系統簡介</h3>
        <p>
          本系統提供統測相關題目的練習，並結合 GPT 技術提供即時解答與分析。
          透過系統化的練習，幫助您更好地準備統測考試。
        </p>
      </div>

      <div className="features-section">
        <h3>主要功能</h3>
        <ul>
          <li>豐富的題庫資源</li>
          <li>即時 GPT 解答與分析</li>
          <li>易錯點提醒</li>
          <li>便捷的問法複製功能</li>
        </ul>
      </div>

      <div className="cta-section">
        <Link to="/practice" className="start-button">
          開始練習
        </Link>
      </div>
    </div>
  );
};

export default Home;
