import React, { useState, useEffect } from 'react';
import {
  Box,
  Container,
  Typography,
  Button,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  TextField,
  IconButton,
  Select,
  MenuItem,
  FormControl,
  InputLabel,
  CircularProgress,
  Alert,
  Card,
  CardContent,
  Grid,
} from '@mui/material';
import { Edit as EditIcon, Delete as DeleteIcon, Add as AddIcon } from '@mui/icons-material';
import axios from 'axios';

const API_URL = 'http://localhost:5000/api';

function ContentManagement() {
  const [contents, setContents] = useState([]);
  const [open, setOpen] = useState(false);
  const [editingContent, setEditingContent] = useState(null);
  const [formData, setFormData] = useState({
    category: '',
    title: '',
    prompt: '',
    steps: [],
  });
  const [newStep, setNewStep] = useState('');
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  const categories = [
    { value: 'course-links', label: '課程相關連結' },
    { value: 'ai-math', label: 'AI怎麼應用在數學' },
    { value: 'exam-structure', label: '會考架構分析' },
    { value: 'ai-practice', label: 'AI實戰' },
    { value: 'prediction', label: '預測考題及個人分析報告' },
  ];

  // 獲取所有內容
  const fetchContents = async () => {
    try {
      setLoading(true);
      const response = await axios.get(`${API_URL}/contents`);
      setContents(response.data);
      setError(null);
    } catch (err) {
      setError('獲取內容失敗：' + err.message);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchContents();
  }, []);

  const handleOpen = (content = null) => {
    if (content) {
      setEditingContent(content);
      setFormData({
        category: content.category,
        title: content.title,
        prompt: content.prompt,
        steps: content.steps,
      });
    } else {
      setEditingContent(null);
      setFormData({
        category: '',
        title: '',
        prompt: '',
        steps: [],
      });
    }
    setOpen(true);
  };

  const handleClose = () => {
    setOpen(false);
    setEditingContent(null);
    setFormData({
      category: '',
      title: '',
      prompt: '',
      steps: [],
    });
    setNewStep('');
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({
      ...prev,
      [name]: value,
    }));
  };

  const handleAddStep = () => {
    if (newStep.trim()) {
      setFormData((prev) => ({
        ...prev,
        steps: [...prev.steps, newStep.trim()],
      }));
      setNewStep('');
    }
  };

  const handleRemoveStep = (index) => {
    setFormData((prev) => ({
      ...prev,
      steps: prev.steps.filter((_, i) => i !== index),
    }));
  };

  const handleSubmit = async () => {
    try {
      if (editingContent) {
        await axios.put(`${API_URL}/contents/${editingContent._id}`, formData);
      } else {
        await axios.post(`${API_URL}/contents`, formData);
      }
      handleClose();
      fetchContents();
    } catch (err) {
      setError('保存內容失敗：' + err.message);
    }
  };

  const handleDelete = async (id) => {
    if (window.confirm('確定要刪除這個內容嗎？')) {
      try {
        await axios.delete(`${API_URL}/contents/${id}`);
        fetchContents();
      } catch (err) {
        setError('刪除內容失敗：' + err.message);
      }
    }
  };

  if (loading) {
    return (
      <Box display="flex" justifyContent="center" alignItems="center" minHeight="200px">
        <CircularProgress />
      </Box>
    );
  }

  return (
    <Container maxWidth="lg" sx={{ py: 4 }}>
      <Typography variant="h4" component="h1" gutterBottom>
        內容管理系統
      </Typography>

      {error && (
        <Alert severity="error" sx={{ mb: 2 }}>
          {error}
        </Alert>
      )}

      <Box sx={{ mb: 4 }}>
        <FormControl fullWidth>
          <InputLabel>選擇類別</InputLabel>
          <Select
            value={formData.category}
            name="category"
            onChange={handleChange}
            label="選擇類別"
          >
            {categories.map((category) => (
              <MenuItem key={category.value} value={category.value}>
                {category.label}
              </MenuItem>
            ))}
          </Select>
        </FormControl>
      </Box>

      <Grid container spacing={3}>
        {contents.map((content) => (
          <Grid item xs={12} md={6} key={content._id}>
            <Card>
              <CardContent>
                <Typography variant="h6" gutterBottom>
                  {content.title}
                </Typography>
                <Typography variant="body1" paragraph>
                  {content.prompt}
                </Typography>
                <Box component="ol" sx={{ pl: 2 }}>
                  {content.steps.map((step, index) => (
                    <Box component="li" key={index} sx={{ mb: 1 }}>
                      <Typography variant="body2">{step}</Typography>
                    </Box>
                  ))}
                </Box>
                <Box sx={{ mt: 2, display: 'flex', gap: 1 }}>
                  <Button
                    variant="outlined"
                    startIcon={<EditIcon />}
                    onClick={() => handleOpen(content)}
                  >
                    編輯
                  </Button>
                  <Button
                    variant="outlined"
                    color="error"
                    startIcon={<DeleteIcon />}
                    onClick={() => handleDelete(content._id)}
                  >
                    刪除
                  </Button>
                </Box>
              </CardContent>
            </Card>
          </Grid>
        ))}
      </Grid>

      <Button
        variant="contained"
        startIcon={<AddIcon />}
        onClick={() => handleOpen()}
        sx={{ mt: 2 }}
      >
        新增內容
      </Button>

      <Dialog open={open} onClose={handleClose} maxWidth="md" fullWidth>
        <DialogTitle>
          {editingContent ? '編輯內容' : '新增內容'}
        </DialogTitle>
        <DialogContent>
          <Box sx={{ mt: 2 }}>
            <FormControl fullWidth sx={{ mb: 2 }}>
              <InputLabel>類別</InputLabel>
              <Select
                value={formData.category}
                name="category"
                onChange={handleChange}
                label="類別"
              >
                {categories.map((category) => (
                  <MenuItem key={category.value} value={category.value}>
                    {category.label}
                  </MenuItem>
                ))}
              </Select>
            </FormControl>

            <TextField
              fullWidth
              label="標題"
              name="title"
              value={formData.title}
              onChange={handleChange}
              sx={{ mb: 2 }}
            />

            <TextField
              fullWidth
              label="提示詞"
              name="prompt"
              value={formData.prompt}
              onChange={handleChange}
              multiline
              rows={4}
              sx={{ mb: 2 }}
            />

            <Box sx={{ mb: 2 }}>
              <Typography variant="subtitle1" gutterBottom>
                步驟
              </Typography>
              {formData.steps.map((step, index) => (
                <Box key={index} sx={{ display: 'flex', alignItems: 'center', mb: 1 }}>
                  <Typography variant="body2" sx={{ flex: 1 }}>
                    {index + 1}. {step}
                  </Typography>
                  <IconButton
                    size="small"
                    onClick={() => handleRemoveStep(index)}
                    color="error"
                  >
                    <DeleteIcon />
                  </IconButton>
                </Box>
              ))}
              <Box sx={{ display: 'flex', gap: 1 }}>
                <TextField
                  fullWidth
                  label="新增步驟"
                  value={newStep}
                  onChange={(e) => setNewStep(e.target.value)}
                  size="small"
                />
                <Button
                  variant="outlined"
                  onClick={handleAddStep}
                  disabled={!newStep.trim()}
                >
                  添加
                </Button>
              </Box>
            </Box>
          </Box>
        </DialogContent>
        <DialogActions>
          <Button onClick={handleClose}>取消</Button>
          <Button onClick={handleSubmit} variant="contained">
            保存
          </Button>
        </DialogActions>
      </Dialog>
    </Container>
  );
}

export default ContentManagement;
