import React, { useState, useEffect } from 'react';
import axios from 'axios';
import '../styles/components/CourseLinks.css';

const API_URL = process.env.REACT_APP_API_URL || 'http://localhost:5000/api';

function CourseLinks() {
  const [links, setLinks] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchLinks = async () => {
      try {
        console.log('Fetching course links...');
        const response = await axios.get(`${API_URL}/contents?category=課程相關連結`);
        console.log('Received course links:', response.data);
        setLinks(response.data);
        setError(null);
      } catch (err) {
        console.error('Error fetching course links:', err);
        setError(err.response?.data?.error || '獲取數據失敗');
      } finally {
        setLoading(false);
      }
    };

    fetchLinks();
  }, []);

  const handleCopy = (text) => {
    navigator.clipboard.writeText(text);
  };

  if (loading) return <div className="loading">載入中...</div>;
  if (error) return (
    <div className="error">
      <p>{error}</p>
      <button onClick={() => window.location.reload()}>重試</button>
    </div>
  );

  return (
    <section className="course-links-section">
      <h2 className="section-title">課程相關連結</h2>
      <div className="links-grid">
        {links.map((link) => (
          <div key={link._id} className="link-card">
            <h3>{link.title}</h3>
            <p>{link.prompt}</p>
            {link.steps && link.steps.length > 0 && (
              <div className="steps">
                <h4>操作步驟：</h4>
                <ol>
                  {link.steps.map((step, index) => (
                    <li key={index}>{step}</li>
                  ))}
                </ol>
              </div>
            )}
            <button 
              className="copy-button"
              onClick={() => handleCopy(link.prompt)}
            >
              複製提示詞
            </button>
          </div>
        ))}
      </div>
    </section>
  );
}

export default CourseLinks;
