import React, { useState, useEffect } from 'react';
import { copyToClipboard } from '../utils/copyToClipboard';
import axios from 'axios';
import '../styles/components/PracticeSection.css';

const API_URL = 'http://localhost:5000/api';

const PracticeSection = () => {
  const [copiedIndex, setCopiedIndex] = useState(null);
  const [practices, setPractices] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchPractices = async () => {
      try {
        setLoading(true);
        const response = await axios.get(`${API_URL}/contents?category=ai-math`);
        setPractices(response.data);
        setError(null);
      } catch (err) {
        setError('獲取內容失敗：' + err.message);
      } finally {
        setLoading(false);
      }
    };

    fetchPractices();
  }, []);

  const handleCopy = (text, index) => {
    copyToClipboard(text);
    setCopiedIndex(index);
    setTimeout(() => setCopiedIndex(null), 2000);
  };

  if (loading) {
    return (
      <div className="practice-section">
        <h2 className="section-title">AI怎麼應用在數學</h2>
        <div className="loading">載入中...</div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="practice-section">
        <h2 className="section-title">AI怎麼應用在數學</h2>
        <div className="error">{error}</div>
      </div>
    );
  }

  return (
    <div className="practice-section">
      <h2 className="section-title">AI怎麼應用在數學</h2>
      <div className="practices-grid">
        {practices.map((practice, index) => (
          <div key={practice._id} className="practice-card">
            <h3 className="practice-title">{practice.title}</h3>
            <div className="prompt-box">
              <pre className="prompt-text">{practice.prompt}</pre>
              <button 
                className="copy-button"
                onClick={() => handleCopy(practice.prompt, index)}
              >
                {copiedIndex === index ? '已複製！' : '複製提示詞'}
              </button>
              <div className="steps-box">
                <h4>操作步驟：</h4>
                <ol>
                  {practice.steps.map((step, stepIndex) => (
                    <li key={stepIndex}>{step}</li>
                  ))}
                </ol>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default PracticeSection;
