import React, { useState } from 'react';
import { questions } from '../data/questions';
import QuestionCard from '../components/QuestionCard';
import ChatPromptBox from '../components/ChatPromptBox';
import GPTResponse from '../components/GPTResponse';
import ReminderNote from '../components/ReminderNote';
import SectionHeader from '../components/SectionHeader';
import '../styles/pages/PracticePage.css';

const PracticePage = () => {
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
  const currentQuestion = questions[currentQuestionIndex];

  const handleNextQuestion = () => {
    if (currentQuestionIndex < questions.length - 1) {
      setCurrentQuestionIndex(currentQuestionIndex + 1);
    }
  };

  const handlePreviousQuestion = () => {
    if (currentQuestionIndex > 0) {
      setCurrentQuestionIndex(currentQuestionIndex - 1);
    }
  };

  return (
    <div className="practice-page">
      <SectionHeader title="練習題目" />
      
      <div className="question-navigation">
        <button 
          onClick={handlePreviousQuestion}
          disabled={currentQuestionIndex === 0}
        >
          上一題
        </button>
        <span className="question-counter">
          第 {currentQuestionIndex + 1} 題 / 共 {questions.length} 題
        </span>
        <button 
          onClick={handleNextQuestion}
          disabled={currentQuestionIndex === questions.length - 1}
        >
          下一題
        </button>
      </div>

      <QuestionCard 
        question={currentQuestion.text}
        imageUrl={currentQuestion.imageUrl}
      />

      <ChatPromptBox prompt={currentQuestion.prompt} />

      <GPTResponse response={currentQuestion.gptResponse} />

      {currentQuestion.reminder && (
        <ReminderNote note={currentQuestion.reminder} />
      )}
    </div>
  );
};

export default PracticePage;
