import React, { useState } from 'react';
import './styles/App.css';
import Home from './Home';
import PracticeSection from './components/PracticeSection';
import ExamStructureSection from './components/ExamStructureSection';
import AIPracticeSection from './components/AIPracticeSection';

function App() {
  const [selectedCategory, setSelectedCategory] = useState(null);

  const renderContent = () => {
    switch (selectedCategory) {
      case 'ai-math':
        return <PracticeSection />;
      case 'exam-structure':
        return <ExamStructureSection />;
      case 'ai-practice':
        return <AIPracticeSection />;
      case 'prediction':
        return <div>預測考題及個人分析報告</div>;
      default:
        return <Home onSelectCategory={setSelectedCategory} />;
    }
  };

  return (
    <div className="app">
      {renderContent()}
    </div>
  );
}

export default App;
