import React, { useState, useEffect } from 'react';
import {
  Box,
  Container,
  Typography,
  Paper,
  Button,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  TextField,
  IconButton,
  Select,
  MenuItem,
  FormControl,
  InputLabel,
  Grid,
  Card,
  CardContent,
  CardActions,
  CircularProgress,
  Alert,
} from '@mui/material';
import { Edit as EditIcon, Delete as DeleteIcon, Add as AddIcon } from '@mui/icons-material';
import axios from 'axios';

const API_URL = 'http://localhost:5000/api';

function ContentManagement() {
  const [contents, setContents] = useState([]);
  const [open, setOpen] = useState(false);
  const [editingContent, setEditingContent] = useState(null);
  const [formData, setFormData] = useState({
    category: '',
    title: '',
    content: '',
    image: '',
  });
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  const categories = [
    { value: 'course-links', label: '課程相關連結' },
    { value: 'ai-math', label: 'AI怎麼應用在數學' },
    { value: 'exam-structure', label: '會考架構分析' },
    { value: 'ai-practice', label: 'AI實戰' },
    { value: 'prediction', label: '預測考題及個人分析報告' },
  ];

  // 獲取所有內容
  const fetchContents = async () => {
    try {
      setLoading(true);
      const response = await axios.get(`${API_URL}/contents`);
      setContents(response.data);
      setError(null);
    } catch (err) {
      setError('獲取內容失敗：' + err.message);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchContents();
  }, []);

  const handleOpen = (content = null) => {
    if (content) {
      setEditingContent(content);
      setFormData({
        category: content.category,
        title: content.title,
        content: content.content,
        image: content.image,
      });
    } else {
      setEditingContent(null);
      setFormData({
        category: '',
        title: '',
        content: '',
        image: '',
      });
    }
    setOpen(true);
  };

  const handleClose = () => {
    setOpen(false);
    setEditingContent(null);
    setFormData({
      category: '',
      title: '',
      content: '',
      image: '',
    });
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({
      ...prev,
      [name]: value,
    }));
  };

  const handleSubmit = async () => {
    try {
      if (editingContent) {
        // 更新現有內容
        await axios.put(`${API_URL}/contents/${editingContent._id}`, formData);
      } else {
        // 添加新內容
        await axios.post(`${API_URL}/contents`, formData);
      }
      handleClose();
      fetchContents(); // 重新獲取內容
    } catch (err) {
      setError('儲存失敗：' + err.message);
    }
  };

  const handleDelete = async (id) => {
    if (window.confirm('確定要刪除這筆內容嗎？')) {
      try {
        await axios.delete(`${API_URL}/contents/${id}`);
        fetchContents(); // 重新獲取內容
      } catch (err) {
        setError('刪除失敗：' + err.message);
      }
    }
  };

  if (loading) {
    return (
      <Box sx={{ display: 'flex', justifyContent: 'center', mt: 4 }}>
        <CircularProgress />
      </Box>
    );
  }

  return (
    <Container maxWidth="lg" sx={{ mt: 4, mb: 4 }}>
      {error && (
        <Alert severity="error" sx={{ mb: 2 }}>
          {error}
        </Alert>
      )}
      
      <Box sx={{ display: 'flex', justifyContent: 'space-between', mb: 3 }}>
        <Typography variant="h4" component="h1">
          內容管理
        </Typography>
        <Button
          variant="contained"
          color="primary"
          startIcon={<AddIcon />}
          onClick={() => handleOpen()}
        >
          新增內容
        </Button>
      </Box>

      <Grid container spacing={3}>
        {contents.map((content) => (
          <Grid item xs={12} md={6} key={content._id}>
            <Card>
              <CardContent>
                <Box sx={{ display: 'flex', alignItems: 'center', mb: 2 }}>
                  <img
                    src={content.image}
                    alt={content.title}
                    style={{
                      width: '60px',
                      height: '60px',
                      marginRight: '16px',
                      filter: 'drop-shadow(0 2px 8px #2196F355)',
                    }}
                  />
                  <Box>
                    <Typography variant="subtitle2" color="text.secondary">
                      {categories.find((cat) => cat.value === content.category)?.label}
                    </Typography>
                    <Typography variant="h6" component="div">
                      {content.title}
                    </Typography>
                  </Box>
                </Box>
                <Typography variant="body2" color="text.secondary">
                  {content.content}
                </Typography>
              </CardContent>
              <CardActions sx={{ justifyContent: 'flex-end' }}>
                <IconButton
                  color="primary"
                  onClick={() => handleOpen(content)}
                >
                  <EditIcon />
                </IconButton>
                <IconButton
                  color="error"
                  onClick={() => handleDelete(content._id)}
                >
                  <DeleteIcon />
                </IconButton>
              </CardActions>
            </Card>
          </Grid>
        ))}
      </Grid>

      <Dialog open={open} onClose={handleClose} maxWidth="md" fullWidth>
        <DialogTitle>
          {editingContent ? '編輯內容' : '新增內容'}
        </DialogTitle>
        <DialogContent>
          <Box sx={{ mt: 2, display: 'flex', flexDirection: 'column', gap: 2 }}>
            <FormControl fullWidth>
              <InputLabel>類別</InputLabel>
              <Select
                name="category"
                value={formData.category}
                onChange={handleChange}
                label="類別"
              >
                {categories.map((category) => (
                  <MenuItem key={category.value} value={category.value}>
                    {category.label}
                  </MenuItem>
                ))}
              </Select>
            </FormControl>
            <TextField
              name="title"
              label="標題"
              value={formData.title}
              onChange={handleChange}
              fullWidth
            />
            <TextField
              name="content"
              label="內容"
              value={formData.content}
              onChange={handleChange}
              multiline
              rows={4}
              fullWidth
            />
            <TextField
              name="image"
              label="圖片網址"
              value={formData.image}
              onChange={handleChange}
              fullWidth
              helperText="請輸入圖片的網址"
            />
            {formData.image && (
              <Box sx={{ mt: 2, textAlign: 'center' }}>
                <img
                  src={formData.image}
                  alt="預覽"
                  style={{
                    maxWidth: '100%',
                    maxHeight: '200px',
                    objectFit: 'contain',
                  }}
                />
              </Box>
            )}
          </Box>
        </DialogContent>
        <DialogActions>
          <Button onClick={handleClose}>取消</Button>
          <Button onClick={handleSubmit} variant="contained" color="primary">
            儲存
          </Button>
        </DialogActions>
      </Dialog>
    </Container>
  );
}

export default ContentManagement;
