import React from 'react';
import './styles/Home.css';

function Home({ onSelectCategory }) {
  const categories = [
    { id: 'ai-math', title: 'AI怎麼應用在數學' },
    { id: 'exam-structure', title: '會考架構分析' },
    { id: 'ai-practice', title: 'AI實戰' },
    { id: 'prediction', title: '預測考題及個人分析報告' }
  ];

  return (
    <div className="home">
      <h1>國中會考數學 AI 輔助學習系統</h1>
      <div className="category-grid">
        {categories.map((category) => (
          <div
            key={category.id}
            className="category-card"
            onClick={() => onSelectCategory(category.id)}
          >
            <h2>{category.title}</h2>
          </div>
        ))}
      </div>
    </div>
  );
}

export default Home;
