import React, { useState, useEffect } from 'react';
import { Box, Typography, Card, CardContent, Button, CircularProgress, Alert } from '@mui/material';
import ContentCopyIcon from '@mui/icons-material/ContentCopy';
import axios from 'axios';
import '../styles/components/PracticeSection.css';

const API_URL = 'http://localhost:5000/api';

const AIPracticeSection = () => {
  const [practices, setPractices] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchPractices = async () => {
      try {
        const response = await axios.get(`${API_URL}/contents?category=ai-practice`);
        setPractices(response.data);
        setError(null);
      } catch (err) {
        setError('獲取AI練習內容失敗：' + err.message);
      } finally {
        setLoading(false);
      }
    };

    fetchPractices();
  }, []);

  const handleCopy = (text) => {
    navigator.clipboard.writeText(text);
  };

  if (loading) {
    return (
      <Box display="flex" justifyContent="center" alignItems="center" minHeight="200px">
        <CircularProgress />
      </Box>
    );
  }

  if (error) {
    return (
      <Box p={2}>
        <Alert severity="error">{error}</Alert>
      </Box>
    );
  }

  return (
    <div className="practice-section">
      <h2 className="section-title">AI實戰</h2>
      <div className="practices-grid">
        {practices.map((practice, index) => (
          <Card key={practice._id || index} sx={{ mb: 2 }}>
            <CardContent>
              <Typography variant="h6" gutterBottom>
                {practice.title}
              </Typography>
              <Typography variant="body1" paragraph>
                {practice.prompt}
              </Typography>
              <Box component="ol" sx={{ pl: 2 }}>
                {practice.steps.map((step, stepIndex) => (
                  <Box component="li" key={stepIndex} sx={{ mb: 1 }}>
                    <Typography variant="body2">{step}</Typography>
                  </Box>
                ))}
              </Box>
              <Button
                variant="outlined"
                startIcon={<ContentCopyIcon />}
                onClick={() => handleCopy(practice.prompt)}
                size="small"
              >
                複製提示詞
              </Button>
            </CardContent>
          </Card>
        ))}
      </div>
    </div>
  );
};

export default AIPracticeSection;
