import React, { useState } from 'react';
import { PracticeSection } from './components/PracticeSection';
import { ExamStructureSection } from './components/ExamStructureSection';
import { AIPracticeSection } from './components/AIPracticeSection';
import './styles/App.css';

function App() {
  const [selectedCategory, setSelectedCategory] = useState('ai-math');

  const handleCategoryChange = (category) => {
    setSelectedCategory(category);
  };

  return (
    <div className="app">
      <nav className="nav-menu">
        <button
          className={`nav-button ${selectedCategory === 'ai-math' ? 'active' : ''}`}
          onClick={() => handleCategoryChange('ai-math')}
        >
          AI怎麼應用在數學
        </button>
        <button
          className={`nav-button ${selectedCategory === 'exam-structure' ? 'active' : ''}`}
          onClick={() => handleCategoryChange('exam-structure')}
        >
          會考架構分析
        </button>
        <button
          className={`nav-button ${selectedCategory === 'ai-practice' ? 'active' : ''}`}
          onClick={() => handleCategoryChange('ai-practice')}
        >
          AI實戰
        </button>
        <button
          className={`nav-button ${selectedCategory === 'prediction' ? 'active' : ''}`}
          onClick={() => handleCategoryChange('prediction')}
        >
          預測考題及個人分析報告
        </button>
      </nav>

      <main className="main-content">
        {selectedCategory === 'ai-math' && <PracticeSection />}
        {selectedCategory === 'exam-structure' && <ExamStructureSection />}
        {selectedCategory === 'ai-practice' && <AIPracticeSection />}
        {selectedCategory === 'prediction' && <div>預測考題及個人分析報告（開發中）</div>}
      </main>
    </div>
  );
}

export default App;
